/*
*
* 현금영수증 객체
*
* 자세한 설명은 아래의 링크를 참고해 주시기 바랍니다.
* - https://developers.popbill.com/reference/cashbill/java/api/object#Cashbill_Input
*
*/

{
    // [필수] 현금영수증 문서번호,
    "mgtKey" : "20211101-001",

    // 현금영수증 거래일시
    // - 당일 과 전일만 입력 가능
    // - 공백이나 미입력시 발행일시 사용
    // - 형식 : yyyyMMddHHmmss
    "tradeDT" : "",

    // [필수] 문서형태, (승인거래, 취소거래) 중 기재
    "tradeType" : "승인거래",

    // [필수] 거래구분, (소득공제용, 지출증빙용) 중 기재
    "tradeUsage" : "소득공제용",

    // [필수] 거래유형, (일반, 도서공연, 대중교통) 중 기재
    "tradeOpt" : "일반",

    // [필수] 과세형태, (과세, 비과세) 중 기재
    "taxationType" : "과세",

    // [필수] 거래금액, ','콤마 불가 숫자만 가능
    "totalAmount" : "11000",

    // [필수] 공급가액, ','콤마 불가 숫자만 가능
    "supplyCost" : "10000",

    // [필수] 부가세, ','콤마 불가 숫자만 가능
    "tax" : "1000",

    // [필수] 봉사료, ','콤마 불가 숫자만 가능
    "serviceFee" : "0",

    // [필수] 가맹점 사업자번호
    "franchiseCorpNum" : "1234567890",

    // 가맹점 종사업장 식별번호
    "franchiseTaxRegID" : "",

    // 가맹점 상호
    "franchiseCorpName" : "발행자 상호",

    // 가맹점 대표자 성명
    "franchiseCEOName" : "발행자 대표자명",

    // 가맹점 주소
    "franchiseAddr" : "발행자 주소",

    // 가맹점 전화번호
    "franchiseTEL" : "070-1234-1234",

    // [필수] 식별번호, 거래구분에 따라 작성
    // 소득공제용 - 주민등록/휴대폰/카드번호 기재가능
    // 지출증빙용 - 사업자번호/주민등록/휴대폰/카드번호 기재가능
    "identityNum" : "154420201111111111",

    // 주문자명
    "customerName" : "주식회사주문자명담당자",

    // 주문상품명
    "itemName" : "상품명",

    // 주문주문번호
    "orderNumber" : "주문번호",

    // 주문자 이메일
    // 팝빌 테스트환경에서 테스트하는 경우에도 안내 메일이 전송되므로,
    // 실제 거래처의 메일주소가 기재되지 않도록 주의
    "email" : "test@test.com",

    // 주문자 휴대폰
    "hp" : "010-111-222",

    // 발행시 알림문자 전송여부
    "smssendYN" : false,

    // 현금영수증 발행 안내메일 제목
    // 미입력시 팝빌에서 설정한 기본값 사용
    "emailSubject" : ""
}

{
    // [필수] 현금영수증 문서번호,
    "mgtKey" : "20211101-001",

    // 현금영수증 거래일시
    // - 당일 과 전일만 입력 가능
    // - 공백이나 미입력시 발행일시 사용
    // - 형식 : yyyyMMddHHmmss
    "tradeDT" : "",

    // 당초 승인 현금영수증의 국세청 승인번호
    // - 'CashbillInfo'의 변수 'confirmNum'를 통해 내용 확인 가능
    "orgConfirmNum" : "K00000002",

    // 당초 승인 현금영수증의 거래일자 (형식 : yyyyMMdd)
    // - 'CashbillInfo'의 변수 'tradeDate'를 통해 내용 확인 가능
    "orgTradeDate" : "20211126",

    // 발행시 알림문자 전송여부
    "smssendYN" : false,

    // 현금영수증 상태 이력을 관리하기 위한 메모
    "memo" : "메모",

    // 현금영수증 취소유형 : true / false 중 택 1
    // - true = 부분 취소, false = 전체 취소
    // - 미입력시 기본값 false 처리
    "isPartCancel" : true,

    // 현금영수증 취소사유 : 1 / 2 / 3 중 택 1
    // - 1 = 거래취소, 2 = 오류발급취소, 3 = 기타
    // - 미입력시 기본값 1 처리
    "cancelType" : "1",

    // 공급가액
    // - {isPartCancel} 값이 true 인 경우 취소할 공급가액 입력
    // - {isPartCancel} 값이 false 인 경우 미입력
    "supplyCost" : "10000",

    // 부가세
    // - {isPartCancel} 값이 true 인 경우 취소할 부가세 입력
    // - {isPartCancel} 값이 false 인 경우 미입력
    "tax" : "1000",

    // 봉사료
    // - {isPartCancel} 값이 true 인 경우 취소할 봉사료 입력
    // - {isPartCancel} 값이 false 인 경우 미입력
    "serviceFee" : "0",

    // 거래금액 (공급가액+부가세+봉사료)
    // - {isPartCancel} 값이 true 인 경우 취소할 거래금액 입력
    // - {isPartCancel} 값이 false 인 경우 미입력
    "totalAmount" : "11000",

    // 현금영수증 발행 안내메일 제목
    // 미입력시 팝빌에서 설정한 기본값 사용
    "emailSubject" : ""
}
